mapper.insert:
        INSERT INTO FAQ
			(
			    NO
			    ,TITLE
			    ,CONTENT
			    ,WRITER_NAME
			    ,CATEGORY_NAME
			    , ENROLL_DATE
			)
			VALUES
			(
			    SEQ_FAQ_NO.NEXTVAL
			    , #{title}
			    , #{content}
			    , #{writeName}
			    , #{categoryName}
			    , SYDATE
			)

mapper.select:
SELECT NAME
FROM FAQ_CATEGORY
WHERE NO = #{categoryNo}
