mapper.select:
SELECT NO ,NAME ,INTRO ,ENROLL_DATE FROM SMALL_GROUP WHERE 1=1
AND ENROLL_DATE = #{enrollDate}
AND PLACE = #{place}
ORDER BY ENROLL_DATE DESC

SELECT COUNT(1) FROM SMALL_GROUP WHERE 1=1
AND ENROLL_DATE = #{enrollDate}
AND PLACE = #{place}

mapper.insert:
INSERT INTO SMALL_GROUP (
    NO
    ,M_NO
    ,C_NO
    ,L_NO
    ,NAME
    ,INTRO
    ,EXINTRO
    ,PLACE
    ,PEOPLENUM
    ,STARTTIME
    ,FINISHTIME
    ,GR_CLOSE
    ,TERM
    ,APPLYDATE
    ,HIT
    ,DEL_YN
    ,REPORT_YN
    ,BLOCK_YN
    ,ENROLL_DATE
) VALUES (
    SMALL_GROUP_SEQ.NEXTVAL
    ,#{mNo}
    ,#{cNo}
    ,#{lNo}
    ,#{name}
    ,#{intro}
    ,#{exintro}
    ,#{place}
    ,#{peoplenum}
    ,#{starttime}
    ,#{finishtime}
    ,#{grClose}
    ,#{term}
    ,#{applydate}
    ,#{hit}
    ,#{delYn}
    ,#{reportYn}
    ,#{blockYn}
    ,#{enrollDate}
)

INSERT INTO GROUP_PO (
NO
,G_NO
,ORIGIN_NAME
,CHANGE_NAME
) VALUES (
GROUP_PO_SEQ.NEXTVAL
,SMALL_GROUP_SEQ.CURRVAL
,#{originName}
,#{changeName}
)

mapper.delete:
UPDATE SMALL_GROUP
SET
STATUS = 'X'
, MODIFY_DATE = SYSDATE
WHERE   NO = #{no}
AND     M_NO = #{mNo}
