mapper.insert:
INSERT INTO MEMBER
(
	NO
	,NAME
	,ID
	,PWD
	,NICK
	,PHONE
	,RESIDENT
	,RESIDENT2
	,EMAIL
	,EMAIL2
	,GENDER
	,ATTACH
	,LOCAL
	,PROFILE
)
VALUES
(
	SEQ_MEMBER_NO.NEXTVAL
	,#{name}
	,#{id}
	,#{pwd}	
	,#{nick}	
	,#{phone}
	,#{resident}
	,#{resident2}
	,#{email}
	,#{email2}
	,#{gender}
	,#{attach}
	,#{local}
	,#{profileName}	
)

mapper.select:

SELECT COUNT(*)
FROM MEMBER
WHERE ID = #{id}


SELECT COUNT(*)
FROM MEMBER
WHERE NICK = #{nick}
