mapper.insert:
INSERT INTO MEMBER
(
	NO
	,LOCAL
	,EMAIL2
	,ID
	,PWD
	,NAME
	,PHONE
	,NICK
	,GENDER
	,EMAIL
	,RESIDENT
	,RESIDENT2
	,ATTACH
	,PROFILE
)
VALUES
(
	SEQ_MEMBER_NO.NEXTVAL
	,#{local}
	,#{email2}
	,#{id}
	,#{pwd}
	,#{name}
	,#{phone}
	,#{nick}
	,#{gender}
	,#{email}
	,#{resident}
	,#{resident2}
	,#{attach}
	,#{profile}	
)

mapper.select:

SELECT COUNT(*)
FROM MEMBER
WHERE ID = #{id}
